import React from "react";

export default function Hide(props) {
  const [hide, setHide] = React.useState(true);

  function handleToggleClick() {
    setHide({
      hide: false,
    });
  }

  return (
    <div>
      <button onClick={handleToggleClick}>Cli</button>
      <p> {hide} Hey i am hiding</p>
    </div>
  );
}
