        setName(event.target.value);
    }
    function handleSubmit(event){
        event.preventDefault();
    }
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
        Name:
        <input type="text" name={name} onChange={handleChange} />
        </label>
        <input type="submit" value="Submit"/>
        <p>{name}</p>
      </form>
    </div>
  )
}
