import React from 'react'

export default function Topic(props) {
    const sidebar = (
        <ul>
            {props.details.map((post)=>
                <li key={post.id}>
                    {post.title}
                </li>
            )}
        </ul>
    )
    const content = props.details.map((post)=>
    <div key={post.id}>
        <h3>{post.title}</h3>
        <p>{post.content}</p>
    </div>
    );
  return (
    <div>
      {sidebar}
      <hr />
      {content}
    </div>
  )
}
